  const [currentPlayer, setCurrentPlayer] = useState('X');
  console.log(game);

  return (
    <div className="App">
      <main className="game">
        {game.map((square, index) => {
          return (
            <div key={ `square ${index}` } className={ `square ${index}` }>{square}</div>
          );
        })}
      </main>
      <footer>
        <div>
          <p className="p1">PLAYER1 (X)</p>
          <p>0</p>
        </div>
        <div>
          <p className="tie">TIE</p>
          <p>0</p>
        </div>
        <div>
          <p className="p2">PLAYER2 (O)</p>
          <p>0</p>
        </div>
      </footer>
    </div>
  );
}

export default App;
